    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/hospital", "root", "dps123");
            String toid = jTextField1.getText();
            String tname = jTextField2.getText();
            String tgender = "";
            if (jRadioButton1.isSelected() == true) {
                tgender = "M";
            }
            if (jRadioButton2.isSelected() == true) {
                tgender = "F";
            }

            String tfather = jTextField3.getText();
            int tage = Integer.parseInt(jTextField4.getText());

            String tdepartment = jTextField5.getText();
            int tcontact = Integer.parseInt(jTextField4.getText());

            String taddress = jTextArea1.getText();

            String strSQL = "update staff set name='" + (tname) + "',gender='" + (tgender) + "',age='" + (tage) + "',fathers_name='" + (tfather) + "',department=' " + (tdepartment) + " ',contact_no='" + (tcontact) + "',address='" + (taddress) + "' where staff_id='" + (toid) + "';";
            int re = con.createStatement().executeUpdate(strSQL);
            if (re == 0) {
                JOptionPane.showMessageDialog(this, "Record doesn't exist", "info", JOptionPane.INFORMATION_MESSAGE);
            } else {
                JOptionPane.showMessageDialog(this, "Record Updated \n" + re + "Rows Effected", "info", JOptionPane.INFORMATION_MESSAGE);
            }

            con.close();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage(), "Error!", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton4ActionPerformed

private void jList1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jList1MouseClicked
    try {
        Class.forName("com.mysql.jdbc.Driver");
        Connection con = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/hospital", "root", "dps123");
        String nid = (String) jList1.getSelectedValue();

        ResultSet rs = con.createStatement().executeQuery("Select * from staff where staff_id='" + (nid) + "'");
        while (rs.next()) {

            String tname = rs.getString("name");
            String tgender = rs.getString("gender");
            if (tgender.equals("M")) {
                jRadioButton1.setSelected(true);
                jRadioButton2.setSelected(false);

            }
            if (tgender.equals("F")) {
                jRadioButton1.setSelected(false);
                jRadioButton2.setSelected(true);
            }


            int tage = rs.getInt("age");
            String tfather = rs.getString("fathers_name");

            String tdepartment = rs.getString("department");
            int tcontact = rs.getInt("contact_no");
            String taddress = rs.getString("address");
            jTextField1.setText("" + nid);
            jTextField2.setText("" + tname);
            jTextField3.setText("" + tfather);
            jTextField4.setText("" + tage);
            jTextField5.setText("" + tdepartment);
            jTextField6.setText("" + tcontact);
            jTextArea1.setText("" + taddress);

        }
        con.close();
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
    }

}//GEN-LAST:event_jList1MouseClicked

private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
    try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/hospital", "root", "dps123");
            String toid = jTextField1.getText();
            String tname = jTextField2.getText();
            String tgender = "";
            if (jRadioButton1.isSelected() == true) {
                tgender = "M";
            }
            if (jRadioButton2.isSelected() == true) {
                tgender = "F";
            }

            String tfather = jTextField3.getText();
            int tage = Integer.parseInt(jTextField4.getText());

            String tdepartment = jTextField5.getText();
            int tcontact = Integer.parseInt(jTextField4.getText());

            String taddress = jTextArea1.getText();
String nstatus="N";
            String strSQL = "update staff set name='" + (tname) + "',gender='" + (tgender) + "',age='" + (tage) + "',fathers_name='" + (tfather) + "',department=' " + (tdepartment) + " ',contact_no='" + (tcontact) + "',address='" + (taddress) + "',status='"+(nstatus)+"' where staff_id='" + (toid) + "';";
            int re = con.createStatement().executeUpdate(strSQL);
            if (re == 0) {
                JOptionPane.showMessageDialog(this, "Record doesn't exist", "info", JOptionPane.INFORMATION_MESSAGE);
            } else {
                JOptionPane.showMessageDialog(this, "Record Deleted \n" + re + "Rows Effected", "info", JOptionPane.INFORMATION_MESSAGE);
            }

            con.close();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage(), "Error!", JOptionPane.ERROR_MESSAGE);
        }
}//GEN-LAST:event_jButton5ActionPerformed

